import Head from "next/head";
import VendorLayout from "@/components/layout-vendor";
import SearchVendor from "@/components/search-vendor";
import Table from "@/components/table";
import axios from "axios";
import { useEffect } from "react";
import { useRouter } from "next/router";

export default function ProductList () {

    const router = useRouter();

    const verifyToken = async () => {
      const url = new URLSearchParams({
        token: localStorage.getItem('token')
      })
      axios.get(
        "http://localhost:8080/api/auth/?" + url
      ).then((response) => {
        console.log("refreshed");
      }).catch((err) => {
        localStorage.removeItem("token");
        router.push("/vendor");
      })
    }
    useEffect(()=>{
      const token = localStorage.getItem("token");
      if (!token) {
        router.push("/vendor");
      } else {
        try {
          verifyToken();
        } catch (err) {
          router.push("/vendor");
        }
      }
    }, []);
  
  
  



    return (
        <>
            <Head>
            <meta name="description" content="Generated by create next app" />
            <meta name="viewport" content="width=device-width, initial-scale=1" />
            <link rel="icon" href="/logo.png" />
            <title>Madrasda | Product List</title>
            </Head>

            <VendorLayout>
                <main className="body-font font-algeria overflow-hidden
                                 md:ml-36">
                <div className="px-5 my-10 mx-auto">
                <h1 className="text-3xl text-primary 
                               md:ml-20 md:mt-10">VIEW PRODUCTS</h1>
                

                <div className="flex justify-start items-center mt-6 text-sm p-2
                                md:ml-20 md:mr-20 md:justify-end md:mt-0">
                    <SearchVendor />
                </div>
                <div className='mt-4 md:ml-20'>
                    <Table />
                </div>
                </div>
                </main>
            </VendorLayout>
        </>
    )
}